public class MultiThreadingWithoutSynchronisation {    public static void main(String[] args) throws InterruptedException {         PrintDemo pd = new PrintDemo();         ThreadDemo demo = new ThreadDemo("Thread-1", pd);         ThreadDemo demo1 = new ThreadDemo("Thread-2", pd);        demo.start();        demo1.start();        for (int i = 0; i < 5; i++) {            System.out.println("Main Thread");        }         demo.join();         demo1.join();    }}class  PrintDemo{    public void printCount(String threadName) {        try {            for(int i = 5; i > 0; i--) {                System.out.println(" Counter   ---   "  + i  +"**********"+ threadName);            }        }catch (Exception e) {            System.out.println("Thread  interrupted.");        }    }}class ThreadDemo  extends Thread{    private  Thread t;    private final String threadName;    private final PrintDemo pd;       public ThreadDemo (String threadName, PrintDemo pd){           this.threadName=threadName;           this.pd=pd;       }    @Override    public void run() {        synchronized (this.pd){            pd.printCount(this.threadName);        }        System.out.println("Thread " +  threadName + " exiting.");    }    public void start(){          if(t==null){              t = new Thread(this,threadName);              t.start();          }    }}